def _get_hostvdisk_mappings(self, host_name):
    """Return the defined storage mappings for a host."""

    return_data = {}
    ssh_cmd = ['svcinfo', 'lshostvdiskmap', '-delim', '!', host_name]
    out, err = self._run_ssh(ssh_cmd)

    mappings = out.strip().split('\n')
